<script>
   ;
        document.addEventListener("DOMContentLoaded", function () {
        const drawer = document.getElementById("drawer-navigation");
        const drawerToggle = document.getElementById("drawer");
        const drawerClose = document.querySelector("[data-drawer-hide]");

        // Function to toggle the drawer
        function toggleDrawer() {
            drawer.classList.toggle("-translate-x-full");
        }

        // Open drawer when clicking the toggle button
        drawerToggle.addEventListener("click", function (event) {
            event.stopPropagation(); // Prevent click from propagating to the document
            toggleDrawer();
        });

        // Close drawer when clicking the close button
        drawerClose.addEventListener("click", toggleDrawer);

        // Close drawer when clicking outside of it
        document.addEventListener("click", function (event) {
            if (!drawer.contains(event.target) && !drawerToggle.contains(event.target)) {
                if (!drawer.classList.contains("-translate-x-full")) {
                    toggleDrawer();
                }
            }
        });
    });
</script>
